import React from 'react';
import {Route} from 'react-router-dom';
import {connect} from "react-redux";

import CollectionsOverview from "../../components/collections-overview/collections-overview.component";
import CollectionPage from "../collection/collection.component";
// import {firestore, convertCollectionsSnapshotToMap} from "../../firebase/firebase.utils";
import {fetchCollectionsStartAsync} from "../../redux/shop/shop.actions";
import WithSpinner from "../../components/with-spinner/with-spinner.component";
import {createStructuredSelector} from "reselect";
import {selectIsCollectionFetching, selectIsCollectionsLoaded} from "../../redux/shop/shop.selector";

const CollectionsOverviewWithSpinner = WithSpinner(CollectionsOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends React.Component {

  componentDidMount() {
    const {fetchCollectionsStartAsync} = this.props;

    fetchCollectionsStartAsync();




    // const collectionRef = firestore.collection('collections');

    /* OBSERVABLE PATTERN */
    // this.unsubscribeFromSnapshot = collectionRef.onSnapshot(async snapshot => {
    //  const collectionsMap = convertCollectionsSnapshotToMap(snapshot);
    //  // console.log(collectionsMap);
    //   updateCollections(collectionsMap);
    //  this.setState({loading: false})
    // })

    /* API Call pattern */
    // fetch("https://firestore.googleapis.com/v1/projects/clothing-store-bdf48/databases/(default)/documents/collections")
    //   .then(response => response.json())
    //   .then(collections => console.log(collections))
    //

    /* promise pattern */
    // collectionRef.get().then(snapshot => {
    //   const collectionsMap = convertCollectionsSnapshotToMap(snapshot);
    //   updateCollections(collectionsMap);
    //   this.setState({loading: false})
    // })
  }

  render() {
    const {match, isCollectionFetching, selectIsCollectionsLoaded} = this.props;
    return (
      <div>
        <Route exact path={`${match.path}`} render={(props) => <CollectionsOverviewWithSpinner isLoading={selectIsCollectionFetching} {...props}  />}/>
        <Route  path={`${match.path}/:collectionId`}  render={(props) => <CollectionPageWithSpinner isLoading={!selectIsCollectionsLoaded} {...props}  />} />
      </div>
    );
  }
}
const mapStateToProps = createStructuredSelector({
  isCollectionFetching: selectIsCollectionFetching,
  isCollectionsLoaded: selectIsCollectionsLoaded
});

const mapDispatchToProps = dispatch => ({
   fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync())
});

export default connect(mapStateToProps, mapDispatchToProps)(ShopPage);
